CREATE TABLE character (id BIGSERIAL, uuid VARCHAR(255) NOT NULL UNIQUE, name VARCHAR(255) NOT NULL UNIQUE, user_id BIGINT NOT NULL, party_id BIGINT NOT NULL, blocked BOOLEAN DEFAULT 'false' NOT NULL, creation TIMESTAMP NOT NULL, PRIMARY KEY(id));
CREATE TABLE groups (id BIGSERIAL, uuid VARCHAR(255) NOT NULL UNIQUE, groupname VARCHAR(255) NOT NULL UNIQUE, description VARCHAR(255) UNIQUE, creation TIMESTAMP NOT NULL, PRIMARY KEY(id));
CREATE TABLE items (id BIGSERIAL, uuid VARCHAR(255) NOT NULL UNIQUE, name VARCHAR(255) NOT NULL, description VARCHAR(255), shopable VARCHAR(255), price BIGINT, unique_item BOOLEAN DEFAULT 'false' NOT NULL, party_id VARCHAR(255), creation TIMESTAMP NOT NULL, PRIMARY KEY(id));
CREATE TABLE monster (id BIGSERIAL, uuid VARCHAR(255) NOT NULL UNIQUE, name VARCHAR(255) NOT NULL, description VARCHAR(255), party_id VARCHAR(255), creation TIMESTAMP NOT NULL, PRIMARY KEY(id));
CREATE TABLE party (id BIGSERIAL, uuid VARCHAR(255) NOT NULL UNIQUE, name VARCHAR(255) NOT NULL UNIQUE, admin_id BIGINT NOT NULL, creation TIMESTAMP NOT NULL, PRIMARY KEY(id));
CREATE TABLE quests (id BIGSERIAL, uuid VARCHAR(255) NOT NULL UNIQUE, name VARCHAR(255) NOT NULL, description VARCHAR(255), location VARCHAR(255), items VARCHAR(255), item_chance BIGINT, monster VARCHAR(255), monster_chance BIGINT, status VARCHAR(255), party_id VARCHAR(255), creation TIMESTAMP NOT NULL, expires TIMESTAMP NOT NULL, PRIMARY KEY(id));
CREATE TABLE users (id BIGSERIAL, uuid VARCHAR(255) NOT NULL UNIQUE, username VARCHAR(255) NOT NULL UNIQUE, lastname VARCHAR(255) NOT NULL, firstname VARCHAR(255) NOT NULL, email VARCHAR(255) NOT NULL UNIQUE, password VARCHAR(255) NOT NULL, hash VARCHAR(255) NOT NULL, salt VARCHAR(255) NOT NULL, group_id BIGINT NOT NULL, party_id BIGINT, creation TIMESTAMP NOT NULL, PRIMARY KEY(id));
